import { useEffect, useMemo, useState } from "react";
import "./css/base.css";
import "./css/classic.css";
import "./css/arcade.css";
import "./css/cat.css";
import "./css/dog.css";

import { renderStats } from "./js/metrics";
import TiptapEditor from "./TiptapEditor";

/* ---------- metrics helpers (FKGL + Flesch) ---------- */
function countSyllables(word) {
  const w = word.toLowerCase().replace(/[^a-z]/g, "");
  if (!w) return 0;
  if (w.length <= 3) return 1;
  const cleaned = w.replace(/(?:es|ed|e)$/g, "");
  const m = cleaned.match(/[aeiouy]+/g);
  return Math.max(1, m ? m.length : 0);
}
function splitSentences(text) {
  return text.replace(/\s+/g, " ").trim().split(/(?<=[\.!?])\s+/).filter(Boolean);
}
function metricsFromText(text) {
  const sentences = splitSentences(text);
  const words = text.trim().split(/\s+/).filter(t => t.replace(/[^\w']/g, "").length > 0);
  const wordCount = words.length;
  const sentenceCount = Math.max(1, sentences.length);
  let syllables = 0;
  for (const w of words) syllables += countSyllables(w);
  const fkGrade = 0.39 * (wordCount / sentenceCount) + 11.8 * (syllables / wordCount) - 15.59;
  const ease = 206.835 - 1.015 * (wordCount / sentenceCount) - 84.6 * (syllables / wordCount);
  return { words: wordCount, sentences: sentenceCount, grade: isFinite(fkGrade) ? fkGrade : 0, ease: isFinite(ease) ? ease : 0 };
}

/* ---------- theme utility ---------- */
function applyTheme(theme) {
  [document.documentElement, document.body].forEach(el => {
    el.classList.remove("classic", "arcade", "cat", "dog");
    el.classList.add(theme);
  });
}

export default function App() {
  // UI state
  const [theme, setTheme] = useState("classic");
  const [targetGrade, setTargetGrade] = useState(6);
  const [showHardWord, setShowHardWord] = useState(true);

  // TipTap editor instance (set by TiptapEditor via onReady)
  const [editor, setEditor] = useState(null);

  // Plain text from the editor (for metrics)
  const [plainText, setPlainText] = useState("");

  useEffect(() => { applyTheme(theme); }, [theme]);

  // Compute and render stats
  const stats = useMemo(() => metricsFromText(plainText), [plainText]);
  useEffect(() => { renderStats(stats); }, [stats]);

  // Formatting helpers using TipTap commands
  const run = (fn) => () => {
    if (!editor) return;
    fn(editor);
  };

  return (
    <div className="page">
      {/* Header with title on left, theme selector on right */}
      <header className="header" style={{ justifyContent: "space-between" }}>
        <div>
          <h1 className="app-title">Sentence Scratcher</h1>
          <div className="version">Version <span id="app-version">1.0.0</span></div>
        </div>

        <div className="toolbar grid-gap">
          <button className={`chip${theme === "classic" ? " is-active" : ""}`} onClick={() => setTheme("classic")}>Classic</button>
          <button className={`chip${theme === "arcade" ? " is-active" : ""}`} onClick={() => setTheme("arcade")}>Arcade</button>
          <button className={`chip${theme === "cat" ? " is-active" : ""}`} onClick={() => setTheme("cat")}>Cat</button>
          <button className={`chip${theme === "dog" ? " is-active" : ""}`} onClick={() => setTheme("dog")}>Dog</button>
        </div>
      </header>

      {/* Controls row: target grade + hard word toggle */}
      <div className="toolbar grid-gap" style={{ marginTop: "10px" }}>
        <label className="select" title="Target grade level">
          <select
            id="target-grade"
            value={`Grade ${targetGrade}`}
            onChange={e => {
              const v = Number((e.target.value || "").replace(/[^\d]/g, ""));
              setTargetGrade(isNaN(v) ? 6 : v);
            }}
          >
            <option>Grade 6</option>
            <option>Grade 8</option>
            <option>Grade 10</option>
          </select>
        </label>

        <label className="chip">
          <input
            type="checkbox"
            id="hw"
            checked={showHardWord}
            onChange={e => setShowHardWord(e.target.checked)}
          />{" "}
          Likely hard word
        </label>
      </div>

      {/* Rich text toolbar (TipTap commands) */}
      <div className="toolbar grid-gap" style={{ marginTop: "10px" }}>
        <button className="btn" onClick={run(ed => ed.chain().focus().toggleBold().run())}><strong>B</strong></button>
        <button className="btn" onClick={run(ed => ed.chain().focus().toggleItalic().run())}><em>I</em></button>
        <button className="btn" onClick={run(ed => ed.chain().focus().toggleHeading({ level: 1 }).run())}>H1</button>
        <button className="btn" onClick={run(ed => ed.chain().focus().toggleHeading({ level: 2 }).run())}>H2</button>
        <button className="btn" onClick={run(ed => ed.chain().focus().toggleBulletList().run())}>• List</button>
        <button className="btn" onClick={run(ed => ed.chain().focus().toggleOrderedList().run())}>1. List</button>
        <button className="btn" onClick={run(ed => ed.chain().focus().unsetAllMarks().clearNodes().run())}>Clear</button>
      </div>

      {/* Stats row */}
      <div className="toolbar grid-gap" style={{ marginTop: "10px" }}>
        <span className="chip">Words, <span data-stat="words">0</span></span>
        <span className="chip">Sentences, <span data-stat="sentences">0</span></span>
        <span className="chip">Grade, <span data-stat="grade">—</span></span>
        <span className="chip">Ease, <span data-stat="ease">—</span></span>
      </div>

      {/* TipTap editor panel */}
      <div className="surface editor-shell">
        <TiptapEditor
          targetGrade={targetGrade}
          showHardWord={showHardWord}
          onReady={setEditor}
          onPlainTextChange={setPlainText}
          // The TipTap component will:
          // - apply sentence highlights with .hl-slight (yellow) and .hl-very (red)
          // - underline likely hard words with .hl-hardword (purple dotted)
          // - attach plain-language tooltip titles
          // - read exemptions from /exemptProperNouns.json
        />
      </div>

      {/* Need help accordion */}
      <details className="accordion" style={{ marginTop: "10px" }}>
        <summary><span className="summary-chip">Need help? Learn to plain language like a pro</span></summary>
        <div className="surface" style={{ marginTop: "10px", padding: "14px" }}>
          <p className="muted">Tip: Use short sentences, everyday words, and active voice. Target the lowest grade level that still conveys your meaning accurately.</p>
        </div>
      </details>
    </div>
  );
}
