// src/TiptapEditor.jsx

import "use-sync-external-store/shim";
import React, { useEffect, useState, useCallback, useRef } from "react";
import { useEditor, EditorContent } from "@tiptap/react";
import StarterKit from "@tiptap/starter-kit";
import Highlight from "@tiptap/extension-highlight";
import Placeholder from "@tiptap/extension-placeholder";
import {
  analyzeSentence,
  countWords,
  countSentences,
  calculateFleschKincaidGrade,
  calculateFleschReadingEase,
} from "./readabilityUtils";

/** scoring */
const MODE = "hemingway";
const ENABLE_BLUE = false;

/* thresholds */
const LONG_SENTENCE_WORDS = 15;       // used only if ENABLE_BLUE is true
const HEMI_HARD_WORDS = 20;           // yellow at 20+ words
const HEMI_VERY_HARD_WORDS = 25;      // red at 25+ words
const DELTA_HARD = 1;                  // yellow if grade > target + 1 and words >= 14
const DELTA_VERY_HARD = 3;             // red if grade > target + 3 and words >= 18
const MIN_YELLOW_WORDS_FOR_GRADE = 14;
const MIN_RED_WORDS_FOR_GRADE = 18;

const DEBOUNCE_MS = 200;

/* colors */
const COLOR_MAP = {
  yellow: { bg: "#fff3b0", border: "#facc15" },
  red:    { bg: "#ffd1d1", border: "#f87171" },
  blue:   { bg: "#dbeafe", border: "#93c5fd" },
};

/* extend Highlight so it renders data-tip and title */
const TipHighlight = Highlight.extend({
  addAttributes() {
    return {
      ...this.parent?.(),
      dataTip: {
        default: null,
        renderHTML: attrs => attrs.dataTip ? { "data-tip": attrs.dataTip } : {},
      },
      title: {
        default: null,
        renderHTML: attrs => attrs.title ? { title: attrs.title } : {},
      },
    };
  },
});

export default function TiptapEditor({ targetGrade }) {
  const [stats, setStats] = useState({ wordCount: 0, sentenceCount: 0, grade: 0, ease: 0 });
  const lastSignature = useRef("");

  const editor = useEditor({
    extensions: [
      StarterKit,
      TipHighlight.configure({ multicolor: true }),
      Placeholder.configure({ placeholder: "Start typing, or paste your text here", includeChildren: true }),
    ],
    content: "",
    editorProps: {
      attributes: {
        class:
          "editor-content prose prose-sm sm:prose lg:prose-lg xl:prose-xl max-w-none p-4 border rounded bg-white focus:outline-none",
      },
    },
  });

  const debounce = (fn, ms) => { let t; return (...a) => { clearTimeout(t); t = setTimeout(() => fn(...a), ms); }; };

  const classify = (words, grade, target) => {
    const delta = grade - target;
    if (words >= HEMI_VERY_HARD_WORDS) return "red";
    if (words >= HEMI_HARD_WORDS) return "yellow";
    if (delta > DELTA_VERY_HARD && words >= MIN_RED_WORDS_FOR_GRADE) return "red";
    if (delta > DELTA_HARD && words >= MIN_YELLOW_WORDS_FOR_GRADE) return "yellow";
    if (ENABLE_BLUE && words >= LONG_SENTENCE_WORDS) return "blue";
    return null;
  };

  const applyHighlight = (from, to, color, tip) => {
    const cfg = COLOR_MAP[color]; if (!cfg) return;
    editor.chain().focus().setTextSelection({ from, to }).setHighlight({
      color: cfg.bg,
      dataTip: tip,
      title: tip,
      HTMLAttributes: {
        style: `background-color:${cfg.bg}; border:1px solid ${cfg.border}; border-radius:2px; padding:0 0.08em; color:inherit;`,
      },
    }).run();
  };

  const analyzeAndHighlight = useCallback(() => {
    if (!editor) return;

    const fullText = editor.getText();
    const signature = `${fullText.length}:${targetGrade}:${MODE}:${ENABLE_BLUE}`;
    if (signature === lastSignature.current) return;
    lastSignature.current = signature;

    setStats({
      wordCount: countWords(fullText),
      sentenceCount: countSentences(fullText),
      grade: calculateFleschKincaidGrade(fullText),
      ease: calculateFleschReadingEase(fullText),
    });

    editor.chain().focus().unsetHighlight().run();

    const { doc } = editor.state;
    doc.descendants((node, pos) => {
      if (!["paragraph", "list_item", "heading"].includes(node.type.name)) return;

      const text = node.textContent; if (!text) return;
      const parts = text.match(/[^.!?…]+[.!?…]*/g) || [];
      let searchFrom = 0;

      for (const raw of parts) {
        const s = raw.trim(); if (!s) { searchFrom += raw.length; continue; }

        const { wordCount, syllableCount, grade } = analyzeSentence(s);
        const fre = 206.835 - 1.015 * wordCount - 84.6 * (syllableCount / Math.max(1, wordCount));

        const delta = grade - targetGrade;
        const byLengthHard = wordCount >= HEMI_VERY_HARD_WORDS;
        const byLengthYellow = wordCount >= HEMI_HARD_WORDS;
        const byDeltaHard = delta > DELTA_VERY_HARD && wordCount >= MIN_RED_WORDS_FOR_GRADE;
        const byDeltaYellow = delta > DELTA_HARD && wordCount >= MIN_YELLOW_WORDS_FOR_GRADE;

        let color = null;
        const reasons = [];
        if (byLengthHard || byDeltaHard) { color = "red"; }
        else if (byLengthYellow || byDeltaYellow) { color = "yellow"; }
        else if (ENABLE_BLUE && wordCount >= LONG_SENTENCE_WORDS) { color = "blue"; }

        if (!color) { searchFrom += raw.length; continue; }

        if (byLengthHard || byLengthYellow) reasons.push("length");
        if (byDeltaHard || byDeltaYellow) reasons.push("above target");

        const why = reasons.length === 2 ? "length and above target" : reasons[0] || "";
        const tip = `Words ${wordCount}, grade ${grade.toFixed(1)}, target ${targetGrade}, FRE ${fre.toFixed(1)}. Reason, ${why}.`;

        const startInNode = text.indexOf(s, searchFrom);
        if (startInNode !== -1) {
          const from = pos + 1 + startInNode;
          const to = from + s.length;
          applyHighlight(from, to, color, tip);
          searchFrom = startInNode + s.length;
        } else {
          searchFrom += raw.length;
        }
      }
    });

    editor.chain().focus().setTextSelection({ from: fullText.length }).run();
  }, [editor, targetGrade]);

  const debounced = useCallback(debounce(analyzeAndHighlight, DEBOUNCE_MS), [analyzeAndHighlight]);

  useEffect(() => {
    if (!editor) return;
    editor.on("update", debounced);
    debounced();
    return () => editor.off("update", debounced);
  }, [editor, debounced]);

  /* JS tooltip that follows the mouse for elements with data-tip */
  useEffect(() => {
    if (!editor) return;
    const root = editor.view.dom;

    const tip = document.createElement("div");
    Object.assign(tip.style, {
      position: "fixed",
      zIndex: "9999",
      maxWidth: "28rem",
      padding: "6px 8px",
      fontSize: "12px",
      lineHeight: "1.25",
      color: "#111827",
      background: "#f9fafb",
      border: "1px solid #e5e7eb",
      borderRadius: "4px",
      boxShadow: "0 4px 10px rgba(0,0,0,.08)",
      pointerEvents: "none",
      display: "none",
    });
    document.body.appendChild(tip);

    const show = (e, text) => {
      tip.textContent = text;
      tip.style.left = `${e.clientX + 10}px`;
      tip.style.top = `${e.clientY + 12}px`;
      tip.style.display = "block";
    };
    const hide = () => { tip.style.display = "none"; };

    const onMove = (e) => {
      const m = e.target.closest('mark[data-tip]');
      if (m && root.contains(m)) {
        show(e, m.getAttribute("data-tip") || "");
      } else {
        hide();
      }
    };

    root.addEventListener("mousemove", onMove);
    root.addEventListener("mouseleave", hide);

    return () => {
      root.removeEventListener("mousemove", onMove);
      root.removeEventListener("mouseleave", hide);
      document.body.removeChild(tip);
    };
  }, [editor]);

  if (!editor) return null;

  return (
    <div className="space-y-4">
      {/* toolbar */}
      <div className="flex flex-wrap gap-2 mb-2">
        <button onClick={() => editor.chain().focus().toggleBold().run()} className="px-2 py-1 border rounded">Bold</button>
        <button onClick={() => editor.chain().focus().toggleItalic().run()} className="px-2 py-1 border rounded">Italic</button>
        <button onClick={() => editor.chain().focus().toggleHeading({ level: 1 }).run()} className="px-2 py-1 border rounded">H1</button>
        <button onClick={() => editor.chain().focus().toggleBulletList().run()} className="px-2 py-1 border rounded">• List</button>
        <button onClick={() => editor.chain().focus().toggleOrderedList().run()} className="px-2 py-1 border rounded">1. List</button>
      </div>

      {/* legend */}
      <div className="flex flex-wrap gap-4 text-xs mb-2">
        <div className="flex items-center gap-1">
          <span className="legend-red w-3 h-3 border inline-block"></span>
          <span>Very hard, 25+ words, or grade +3 with 18+ words</span>
        </div>
        <div className="flex items-center gap-1">
          <span className="legend-yellow w-3 h-3 border inline-block"></span>
          <span>Slightly over, 20+ words, or grade +1 with 14+ words</span>
        </div>
        {ENABLE_BLUE && (
          <div className="flex items-center gap-1">
            <span className="legend-blue w-3 h-3 border inline-block"></span>
            <span>Long, {LONG_SENTENCE_WORDS}+ words</span>
          </div>
        )}
      </div>

      {/* stats */}
      <div className="flex flex-wrap gap-4 text-sm">
        <div className="bg-gray-100 px-2 py-1 rounded">Words, {stats.wordCount}</div>
        <div className="bg-gray-100 px-2 py-1 rounded">Sentences, {stats.sentenceCount}</div>
        <div className="bg-green-100 px-2 py-1 rounded">Grade, {stats.grade.toFixed(1)}</div>
        <div className="bg-blue-100 px-2 py-1 rounded">Ease, {stats.ease.toFixed(1)}</div>
      </div>

      <EditorContent editor={editor} />
    </div>
  );
}
