import React, { useEffect, useState } from "react";

const THEMES = ["classic", "arcade", "cat", "dog"];
const LABELS = {
  classic: "Classic",
  arcade: "Arcade",
  cat: "Cat",
  dog: "Dog",
};

export default function ThemeToggle({ className = "" }) {
  const [theme, setTheme] = useState(() => {
    try { return localStorage.getItem("ssTheme") || "classic"; } catch { return "classic"; }
  });

  const apply = (t) => {
    document.body.classList.toggle("theme-arcade", t === "arcade");
    document.body.classList.toggle("theme-cat", t === "cat");
    document.body.classList.toggle("theme-dog", t === "dog");
    try { localStorage.setItem("ssTheme", t); } catch {}
    setTheme(t);
  };

  useEffect(() => { apply(theme); }, []); // apply on mount

  return (
    <div className={`theme-switch flex gap-2 ${className}`}>
      {THEMES.map((t) => (
        <button
          key={t}
          type="button"
          onClick={() => apply(t)}
          className={`btn theme-btn ${theme === t ? "is-active" : ""}`}
          aria-pressed={theme === t}
          title={`Switch to ${LABELS[t]}`}
        >
          {LABELS[t]}
        </button>
      ))}
    </div>
  );
}
