// SentenceHighlighter.jsx

import React from "react";
import { analyzeSentence } from "./readabilityUtils";

export default function SentenceHighlighter({ text, targetGrade }) {
  const sentenceRegex = /[^.!?]+[.!?]*/g;
  const sentences = text.match(sentenceRegex) || [];

  const getHighlightColor = (grade, wordCount) => {
    if (wordCount >= 25) return "bg-blue-200"; // Very long sentence
    if (grade > targetGrade + 2) return "bg-red-200"; // Significantly over target
    if (grade > targetGrade) return "bg-yellow-200"; // Slightly over target
    return ""; // Normal
  };

  return (
    <div className="space-y-2 mt-4">
      {sentences.map((sentence, i) => {
        const { grade, wordCount } = analyzeSentence(sentence);
        const color = getHighlightColor(grade, wordCount);

        return (
          <span
            key={i}
            className={`inline-block px-1 py-0.5 rounded ${color}`}
            title={`Grade: ${grade}, Words: ${wordCount}`}
          >
            {sentence}
          </span>
        );
      })}
    </div>
  );
}
