// src/TiptapEditor.jsx

import "use-sync-external-store/shim";               // shim for React 18+ hooks
import React, { useEffect, useState, useCallback, useRef } from "react";
import { useEditor, EditorContent } from "@tiptap/react";
import StarterKit from "@tiptap/starter-kit";
import Highlight from "@tiptap/extension-highlight";
import {
  analyzeSentence,
  countWords,
  countSentences,
  calculateFleschKincaidGrade,
  calculateFleschReadingEase,
} from "./readabilityUtils";

export default function TiptapEditor({ targetGrade }) {
  const [stats, setStats] = useState({
    wordCount: 0,
    sentenceCount: 0,
    grade: 0,
    ease: 0,
  });
  const lastText = useRef("");

  const editor = useEditor({
    extensions: [StarterKit, Highlight],
    content: "<p>Start typing here…</p>",
    editorProps: {
      attributes: {
        class:
          "editor-content prose prose-sm sm:prose lg:prose-lg xl:prose-xl max-w-none p-4 border rounded bg-white focus:outline-none",
      },
    },
  });

  // debounce helper
  const debounce = (fn, ms) => {
    let timer;
    return (...args) => {
      clearTimeout(timer);
      timer = setTimeout(() => fn(...args), ms);
    };
  };

  // run analysis + highlight
  const analyzeAndHighlight = useCallback(() => {
    if (!editor) return;
    const text = editor.getText();
    if (text === lastText.current) return;
    lastText.current = text;

    // 1) Stats
    setStats({
      wordCount: countWords(text),
      sentenceCount: countSentences(text),
      grade: calculateFleschKincaidGrade(text),
      ease: calculateFleschReadingEase(text),
    });

    // 2) clear highlights
    editor.chain().focus().unsetHighlight().run();

    // 3) split and highlight
    let offset = 0;
    const sentences = text.match(/[^.!?…]+[.!?…]*/g) || [];

    for (const sentence of sentences) {
      const trimmed = sentence.trim();
      const { grade, wordCount } = analyzeSentence(trimmed);

      let color = null;
      if (wordCount >= 20) color = "blue";
      else if (grade > targetGrade + 1.5) color = "red";
      else if (grade > targetGrade + 0.5) color = "yellow";

      if (color) {
        const start = text.indexOf(trimmed, offset) + 1;
        const end = start + trimmed.length;
        const tooltip = `Grade ${grade.toFixed(1)}, ${wordCount} words — try splitting`;

        editor
          .chain()
          .focus()
          .setTextSelection({ from: start, to: end })
          .setHighlight({
            color,
            HTMLAttributes: { title: tooltip },
          })
          .run();
      }

      offset += sentence.length;
    }

    // 4) reset cursor at end
    editor.chain().focus().setTextSelection({ from: text.length }).run();
  }, [editor, targetGrade]);

  // debounced version
  const debounced = useCallback(debounce(analyzeAndHighlight, 200), [
    analyzeAndHighlight,
  ]);

  // wire up
  useEffect(() => {
    if (!editor) return;
    editor.on("update", debounced);
    debounced();
    return () => editor.off("update", debounced);
  }, [editor, debounced]);

  if (!editor) return null;

  return (
    <div className="space-y-4">
      {/* toolbar */}
      <div className="flex flex-wrap gap-2 mb-2">
        <button
          onClick={() => editor.chain().focus().toggleBold().run()}
          className="px-2 py-1 border rounded"
        >
          Bold
        </button>
        <button
          onClick={() => editor.chain().focus().toggleItalic().run()}
          className="px-2 py-1 border rounded"
        >
          Italic
        </button>
        <button
          onClick={() =>
            editor.chain().focus().toggleHeading({ level: 1 }).run()
          }
          className="px-2 py-1 border rounded"
        >
          H1
        </button>
        <button
          onClick={() => editor.chain().focus().toggleBulletList().run()}
          className="px-2 py-1 border rounded"
        >
          • List
        </button>
        <button
          onClick={() => editor.chain().focus().toggleOrderedList().run()}
          className="px-2 py-1 border rounded"
        >
          1. List
        </button>
      </div>

      {/* legend */}
      <div className="flex flex-wrap gap-4 text-xs mb-2">
        <div className="flex items-center gap-1">
          <span className="legend-yellow w-3 h-3 border inline-block"></span>
          <span>Grade &gt; target + 0.5</span>
        </div>
        <div className="flex items-center gap-1">
          <span className="legend-red w-3 h-3 border inline-block"></span>
          <span>Grade &gt; target + 1.5</span>
        </div>
        <div className="flex items-center gap-1">
          <span className="legend-blue w-3 h-3 border inline-block"></span>
          <span>Sentence ≥ 20 words</span>
        </div>
      </div>

      {/* stats */}
      <div className="flex flex-wrap gap-4 text-sm">
        <div className="bg-gray-100 px-2 py-1 rounded">
          Words: {stats.wordCount}
        </div>
        <div className="bg-gray-100 px-2 py-1 rounded">
          Sentences: {stats.sentenceCount}
        </div>
        <div className="bg-green-100 px-2 py-1 rounded">
          Grade: {stats.grade.toFixed(1)}
        </div>
        <div className="bg-blue-100 px-2 py-1 rounded">
          Ease: {stats.ease.toFixed(1)}
        </div>
      </div>

      {/* editor */}
      <EditorContent editor={editor} />
    </div>
  );
}
