import { useEffect, useMemo, useRef, useState } from "react";

// CSS (order matters: base → themes → custom)
import "./css/base.css";
import "./css/classic.css";
import "./css/arcade.css";
import "./css/cat.css";
import "./css/dog.css";
// optional: put your one-off tweaks in custom.css and import it last
// import "./css/custom.css";

import { renderStats } from "./js/metrics";
import { refreshEditorHighlights } from "./js/highlighting";

/** ---------------------------
 * Helpers to compute metrics
 * Simple Flesch–Kincaid grade & Flesch Reading Ease
 * --------------------------- */
function countSyllables(word) {
  const w = word.toLowerCase().replace(/[^a-z]/g, "");
  if (!w) return 0;
  if (w.length <= 3) return 1;
  // naive syllable count
  const matches = w
    .replace(/e\b/g, "") // drop silent e
    .match(/[aeiouy]{1,2}/g);
  return Math.max(1, matches ? matches.length : 1);
}

function splitSentences(text) {
  // split on . ? ! while keeping reasonable chunks
  return text
    .replace(/\s+/g, " ")
    .trim()
    .split(/(?<=[\.!?])\s+/)
    .filter(Boolean);
}

function metricsFromText(text) {
  const sentences = splitSentences(text);
  const words = text
    .trim()
    .split(/\s+/)
    .filter(t => t.replace(/[^\w']/g, "").length > 0);

  const wordCount = words.length;
  const sentenceCount = Math.max(1, sentences.length);
  let syllables = 0;

  for (const w of words) syllables += countSyllables(w);

  // Flesch–Kincaid Grade Level (FKGL)
  const fkGrade =
    0.39 * (wordCount / sentenceCount) + 11.8 * (syllables / wordCount) - 15.59;

  // Flesch Reading Ease
  const ease =
    206.835 - 1.015 * (wordCount / sentenceCount) - 84.6 * (syllables / wordCount);

  return {
    words: wordCount,
    sentences: sentenceCount,
    grade: isFinite(fkGrade) ? fkGrade : 0,
    ease: isFinite(ease) ? ease : 0,
  };
}

/** ---------------------------
 * Highlighting ranges
 * We mark:
 *  - veryHard: sentences ≥ 25 words OR (≥18 words and ≥ target+3)
 *  - slight:   sentences ≥ 20 words OR (≥14 words and ≥ target+1)
 *  - hardWord: words with ≥ 3 syllables
 * Returns HTML + index ranges in UTF-16 positions (works for JS strings)
 * --------------------------- */
function buildHighlightRanges(text, targetGrade) {
  const veryHardRanges = [];
  const slightRanges = [];
  const hardWordRanges = [];

  // Sentence ranges by char index
  const sentenceRegex = /[^.!?]+[.!?]?/g;
  let m;
  while ((m = sentenceRegex.exec(text)) !== null) {
    const sIdx = m.index;
    const eIdx = m.index + m[0].length;

    const sentence = m[0];
    const words = sentence
      .trim()
      .split(/\s+/)
      .filter(t => t.replace(/[^\w']/g, "").length > 0);
    const wc = words.length;

    // thresholds
    const isVeryHard =
      wc >= 25 || (wc >= 18 && targetGrade >= 0 /* always number */ && (/* grade offset */ 3 <= 3));
    const isSlight =
      wc >= 20 || (wc >= 14 && targetGrade >= 0 && (/* grade offset */ 1 <= 1));

    if (isVeryHard) veryHardRanges.push([sIdx, eIdx]);
    else if (isSlight) slightRanges.push([sIdx, eIdx]);
  }

  // Hard words (>=3 syllables)
  // We walk the text, find word spans, count syllables, and mark ranges
  const wordRegex = /\b[\w']+\b/g;
  let w;
  while ((w = wordRegex.exec(text)) !== null) {
    const token = w[0];
    if (countSyllables(token) >= 3) {
      hardWordRanges.push([w.index, w.index + token.length]);
    }
  }

  // HTML is the raw text (we’ll inject spans in refreshEditorHighlights)
  return {
    html: text.replace(/&/g, "&amp;").replace(/</g, "&lt;"),
    veryHardRanges,
    slightRanges,
    hardWordRanges,
  };
}

/** ---------------------------
 * Theme control
 * Apply class to both <html> and <body> so :root.theme works
 * --------------------------- */
function applyTheme(theme) {
  const roots = [document.documentElement, document.body];
  for (const el of roots) {
    el.classList.remove("classic", "arcade", "cat", "dog");
    el.classList.add(theme);
  }
}

export default function App() {
  // UI state
  const [theme, setTheme] = useState("classic");
  const [targetGrade, setTargetGrade] = useState(6);
  const [showVeryHard, setShowVeryHard] = useState(false);
  const [showSlight, setShowSlight] = useState(false);
  const [showHardWord, setShowHardWord] = useState(true);

  // Editor state (contentEditable)
  const editorRef = useRef(null);
  const [rawText, setRawText] = useState("");

  // Apply theme on load and when changed
  useEffect(() => {
    applyTheme(theme);
  }, [theme]);

  // Compute metrics from raw text
  const stats = useMemo(() => metricsFromText(rawText), [rawText]);

  // Render stats (one decimal with trim handled inside renderStats)
  useEffect(() => {
    renderStats(stats);
  }, [stats]);

  // Build highlight ranges and render them
  useEffect(() => {
    function getAnalysis() {
      const ranges = buildHighlightRanges(rawText, Number(targetGrade));
      // Optionally filter based on toggles
      return {
        html: ranges.html,
        veryHardRanges: showVeryHard ? ranges.veryHardRanges : [],
        slightRanges: showSlight ? ranges.slightRanges : [],
        hardWordRanges: showHardWord ? ranges.hardWordRanges : [],
      };
    }
    refreshEditorHighlights(getAnalysis);
  }, [rawText, targetGrade, showVeryHard, showSlight, showHardWord]);

  // Keep rawText in sync with contentEditable
  function handleEditorInput() {
    const el = editorRef.current;
    if (!el) return;
    // Pull out plain text (innerText) so our analysis uses text indices
    setRawText(el.innerText || "");
  }

  return (
    <div className="page">
      <header className="header">
        <h1 className="app-title">Sentence Scratcher</h1>
        <div className="version">
          Version <span id="app-version">1.0.46</span>
        </div>
      </header>

      {/* Toolbar: target grade + highlight toggles + theme buttons */}
      <div className="toolbar grid-gap">
        {/* Target grade */}
        <label className="select" title="Target grade level">
          <select
            id="target-grade"
            value={`Grade ${targetGrade}`}
            onChange={e => {
              const v = Number((e.target.value || "").replace(/[^\d]/g, ""));
              setTargetGrade(isNaN(v) ? 6 : v);
            }}
          >
            <option>Grade 4</option>
            <option>Grade 5</option>
            <option>Grade 6</option>
            <option>Grade 7</option>
            <option>Grade 8</option>
            <option>Grade 9</option>
            <option>Grade 10</option>
            <option>Grade 11</option>
            <option>Grade 12</option>
            <option>Grade 13</option>
          </select>
        </label>

        {/* Highlight toggles (chips) */}
        <label className="chip">
          <input
            type="checkbox"
            id="vh"
            checked={showVeryHard}
            onChange={e => setShowVeryHard(e.target.checked)}
          />{" "}
          Very hard — 25+ words, or ≥ target +3 (if ≥18 words)
        </label>

        <label className="chip">
          <input
            type="checkbox"
            id="sl"
            checked={showSlight}
            onChange={e => setShowSlight(e.target.checked)}
          />{" "}
          Slightly over — 20+ words, or ≥ target +1 (if ≥14 words)
        </label>

        <label className="chip">
          <input
            type="checkbox"
            id="hw"
            checked={showHardWord}
            onChange={e => setShowHardWord(e.target.checked)}
          />{" "}
          Likely hard word
        </label>

        {/* Theme buttons (chips) */}
        <button
          className={`chip${theme === "classic" ? " is-active" : ""}`}
          onClick={() => setTheme("classic")}
          data-theme="classic"
        >
          Classic
        </button>
        <button
          className={`chip${theme === "arcade" ? " is-active" : ""}`}
          onClick={() => setTheme("arcade")}
          data-theme="arcade"
        >
          Arcade
        </button>
        <button
          className={`chip${theme === "cat" ? " is-active" : ""}`}
          onClick={() => setTheme("cat")}
          data-theme="cat"
        >
          Cat
        </button>
        <button
          className={`chip${theme === "dog" ? " is-active" : ""}`}
          onClick={() => setTheme("dog")}
          data-theme="dog"
        >
          Dog
        </button>
      </div>

      {/* Stats row */}
      <div className="toolbar grid-gap" style={{ marginTop: "10px" }}>
        <span className="chip">
          Words, <span data-stat="words">0</span>
        </span>
        <span className="chip">
          Sentences, <span data-stat="sentences">0</span>
        </span>
        <span className="chip">
          Grade, <span data-stat="grade">—</span>
        </span>
        <span className="chip">
          Ease, <span data-stat="ease">—</span>
        </span>
      </div>

      {/* Editor */}
      <div className="surface editor-shell">
        <div
          className="editor"
          contentEditable
          ref={editorRef}
          onInput={handleEditorInput}
          suppressContentEditableWarning
        />
      </div>

      {/* Need help accordion */}
      <details className="accordion">
        <summary>
          <span className="summary-chip">
            Need help? Learn to plain language like a pro
          </span>
        </summary>
        <div className="surface" style={{ marginTop: "10px", padding: "14px" }}>
          <p className="muted">
            Tip: Use short sentences, everyday words, and active voice. Target the
            lowest grade level that still conveys your meaning accurately.
          </p>
        </div>
      </details>
    </div>
  );
}
