// App.jsx

import React, { useState } from "react";
import SentenceHighlighter from "./SentenceHighlighter";
import StatsPanel from "./StatsPanel";

export default function App() {
  const [text, setText] = useState(
    "This is a simple sentence. This sentence is much longer, contains more complex vocabulary, and may exceed a comfortable reading level for some readers."
  );
  const [targetGrade, setTargetGrade] = useState(8);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Hemingway-Style Editor</h1>

      <div className="mb-4">
        <label className="block font-semibold mb-1">Target Grade Level:</label>
        <select
          value={targetGrade}
          onChange={(e) => setTargetGrade(parseInt(e.target.value))}
          className="border border-gray-300 rounded px-2 py-1"
        >
          {Array.from({ length: 13 }, (_, i) => (
            <option key={i} value={i + 1}>
              Grade {i + 1}
            </option>
          ))}
        </select>
      </div>

      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        rows={10}
        className="w-full border border-gray-300 rounded p-2 font-mono"
        placeholder="Type or paste your text here..."
      />

      <StatsPanel text={text} />
      <SentenceHighlighter text={text} targetGrade={targetGrade} />
    </div>
  );
}
