import React, { useEffect, useState } from "react";
import TiptapEditor from "./TiptapEditor";

// styles (global + themes)
import "./custom.css";
import "./arcade.css";
import "./cat.css";
import "./dog.css";

const VERSION = process.env.REACT_APP_VERSION || "";

export default function App() {
  const [theme, setTheme] = useState("classic"); // classic | arcade | cat | dog
  const [targetGrade, setTargetGrade] = useState(6); // 6, 8, 10
  const [showHardWords, setShowHardWords] = useState(true);

  const [stats, setStats] = useState({
    words: 0,
    sentences: 0,
    grade: null,
    ease: null,
  });

  // apply theme class to <body>
  useEffect(() => {
    const b = document.body;
    b.classList.remove("theme-arcade", "theme-cat", "theme-dog");
    if (theme === "arcade") b.classList.add("theme-arcade");
    if (theme === "cat") b.classList.add("theme-cat");
    if (theme === "dog") b.classList.add("theme-dog");
  }, [theme]);

  return (
    <div className="container">
      {/* HUD — title/version on the left, theme switch on the right */}
      <div className="hud chrome-card">
        <div className="title-stack">
          <h1 className="app-title">Sentence Scratcher</h1>
          <div className="version-badge">{VERSION ? `Version ${VERSION}` : ""}</div>
        </div>

        <div className="theme-switch">
          <button
            className={`theme-btn ${theme === "classic" ? "is-active" : ""}`}
            onClick={() => setTheme("classic")}
          >
            Classic
          </button>
          <button
            className={`theme-btn ${theme === "arcade" ? "is-active" : ""}`}
            onClick={() => setTheme("arcade")}
          >
            Arcade
          </button>
          <button
            className={`theme-btn ${theme === "cat" ? "is-active" : ""}`}
            onClick={() => setTheme("cat")}
          >
            Cat
          </button>
          <button
            className={`theme-btn ${theme === "dog" ? "is-active" : ""}`}
            onClick={() => setTheme("dog")}
          >
            Dog
          </button>
        </div>
      </div>

      {/* Controls row */}
      <div className="top-controls chrome-card">
        <label className="label">Target Grade Level:</label>
        <select
          className="select"
          value={targetGrade}
          onChange={(e) => setTargetGrade(Number(e.target.value))}
        >
          <option value={6}>Grade 6</option>
          <option value={8}>Grade 8</option>
          <option value={10}>Grade 10</option>
        </select>

        <div className="legend-row">
          <span>
            <span className="legend-swatch legend-red" /> Very hard, 25+ words, or grade +3 with 18+ words
          </span>
          <span>
            <span className="legend-swatch legend-yellow" /> Slightly over, 20+ words, or grade +1 with 14+ words
          </span>
        </div>

        <label className="hardwords-toggle">
          <input
            type="checkbox"
            checked={showHardWords}
            onChange={(e) => setShowHardWords(e.target.checked)}
          />
          <span className="aa">Aa</span> Likely hard word
        </label>
      </div>

      {/* Stats */}
      <div className="stats-row">
        <span className="stat-chip">Words, {stats.words ?? 0}</span>
        <span className="stat-chip">Sentences, {stats.sentences ?? 0}</span>
        <span className="stat-chip">
          Grade, {stats.grade == null ? "—" : stats.grade.toFixed(1)}
        </span>
        <span className="stat-chip">
          Ease, {stats.ease == null ? "—" : stats.ease.toFixed(1)}
        </span>
      </div>

      {/* Editor panel */}
      <div className="panel editor-wrap chrome-card">
        <TiptapEditor
          targetGrade={targetGrade}
          showHardWords={showHardWords}
          onStats={(next) => setStats(next)}
        />
      </div>
    </div>
  );
}
