import React, { useEffect, useState } from "react";

const THEMES = ["classic", "arcade", "cat"];
const LABELS = {
  classic: "Theme, Classic",
  arcade: "Theme, Arcade",
  cat: "Theme, Cat",
};

export default function ThemeToggle({ className = "" }) {
  const [theme, setTheme] = useState(() => {
    try { return localStorage.getItem("ssTheme") || "classic"; } catch { return "classic"; }
  });

  // apply body classes
  useEffect(() => {
    document.body.classList.toggle("theme-arcade", theme === "arcade");
    document.body.classList.toggle("theme-cat", theme === "cat");
    try { localStorage.setItem("ssTheme", theme); } catch {}
  }, [theme]);

  const next = () => {
    const idx = THEMES.indexOf(theme);
    setTheme(THEMES[(idx + 1) % THEMES.length]);
  };

  return (
    <button
      type="button"
      onClick={next}
      className={`btn toggle ${className}`}
      title="Switch theme"
      aria-label={LABELS[theme]}
    >
      {LABELS[theme]}
    </button>
  );
}
