// StatsPanel.jsx

import React from "react";
import {
  countWords,
  countSentences,
  calculateFleschKincaidGrade,
  calculateFleschReadingEase,
} from "./readabilityUtils";

export default function StatsPanel({ text }) {
  const wordCount = countWords(text);
  const sentenceCount = countSentences(text);
  const grade = calculateFleschKincaidGrade(text);
  const ease = calculateFleschReadingEase(text);

  return (
    <div className="flex flex-wrap gap-4 mt-4 text-sm">
      <div className="bg-gray-100 px-3 py-1 rounded">Words: {wordCount}</div>
      <div className="bg-gray-100 px-3 py-1 rounded">Sentences: {sentenceCount}</div>
      <div className="bg-green-100 px-3 py-1 rounded">Grade Level: {grade}</div>
      <div className="bg-blue-100 px-3 py-1 rounded">Reading Ease: {ease}</div>
    </div>
  );
}
