// ================================
// Sentence Scratcher - Metrics formatting
// ================================

export function formatOneDecimal(n) {
  if (n == null || isNaN(n)) return "—";
  return Number(n).toFixed(1).replace(/\.0$/, "");
}

export function renderStats({ words, sentences, grade, ease }) {
  const wordsEl = document.querySelector('[data-stat="words"]');
  const sentencesEl = document.querySelector('[data-stat="sentences"]');
  const gradeEl = document.querySelector('[data-stat="grade"]');
  const easeEl = document.querySelector('[data-stat="ease"]');

  if (wordsEl) wordsEl.textContent = String(Math.max(0, words | 0));
  if (sentencesEl) sentencesEl.textContent = String(Math.max(0, sentences | 0));
  if (gradeEl) gradeEl.textContent = formatOneDecimal(grade);
  if (easeEl) easeEl.textContent = formatOneDecimal(ease);
}
