// src/App.jsx
import React, { useEffect, useState } from "react";
import TiptapEditor from "./TiptapEditor";
import "./custom.css";
import "./arcade.css";
import "./cat.css";
import "./dog.css";

const VERSION = process.env.REACT_APP_VERSION || "";

const THEMES = ["classic", "arcade", "cat", "dog"];

export default function App() {
  const [theme, setTheme] = useState("classic");
  const [stats, setStats] = useState({ words: 0, sentences: 0, grade: null, ease: null });
  const [target, setTarget] = useState(6);
  const [showHardWords, setShowHardWords] = useState(true);

  useEffect(() => {
    document.body.classList.remove(
      "theme-classic",
      "theme-arcade",
      "theme-cat",
      "theme-dog"
    );
    document.body.classList.add(`theme-${theme}`);
  }, [theme]);

  return (
    <div className="app-shell">
      {/* HUD / header */}
      <div className="hud chrome-card">
        <div className="hud-row">
          <div className="hud-left">
            <h1 className="app-title">Sentence Scratcher</h1>
            {/* plain version text (no pill) */}
            <div className="version-text">{VERSION ? `Version ${VERSION}` : ""}</div>
          </div>

          <div className="hud-right theme-switch">
            {THEMES.map(t => (
              <button
                key={t}
                className={`theme-btn ${theme === t ? "is-active" : ""}`}
                onClick={() => setTheme(t)}
                aria-pressed={theme === t}
              >
                {t[0].toUpperCase() + t.slice(1)}
              </button>
            ))}
          </div>
        </div>

        {/* Top controls */}
        <div className="top-controls">
          <label className="label">Target Grade Level:</label>
          <select
            value={target}
            onChange={(e) => setTarget(Number(e.target.value))}
          >
            <option value={6}>Grade 6</option>
            <option value={8}>Grade 8</option>
            <option value={10}>Grade 10</option>
          </select>

          <div className="legend-row">
            <span className="legend-swatch legend-red" />
            <span className="legend-text">Very hard&nbsp;— 25+ words, or ≥ target +3 (if ≥18 words)</span>

            <span className="legend-swatch legend-yellow" />
            <span className="legend-text">Slightly over&nbsp;— 20+ words, or ≥ target +1 (if ≥14 words)</span>
          </div>

          <label className="toggle">
            <input
              type="checkbox"
              checked={showHardWords}
              onChange={(e) => setShowHardWords(e.target.checked)}
            />
            <span className="toggle-label">
              <span className="icon-aa" aria-hidden>Aa</span> Likely hard word
            </span>
          </label>
        </div>

        {/* Stats chips */}
        <div className="stats-row">
          <div className="stat-chip">Words, {stats.words ?? 0}</div>
          <div className="stat-chip">Sentences, {stats.sentences ?? 0}</div>
          <div className="stat-chip">Grade, {stats.grade ?? "—"}</div>
          <div className="stat-chip">Ease, {stats.ease ?? "—"}</div>
        </div>
      </div>

      {/* Editor card */}
      <div className="chrome-card">
        <TiptapEditor
          targetGrade={target}
          showHardWords={showHardWords}
          onStats={setStats}
        />
      </div>

      {/* Help accordion lives below the editor (unchanged) */}
      <details className="help-card">
        <summary className="help-summary">Need help? Learn to plain language like a pro</summary>
        <div className="help-body">
          <div className="help-section">
            <h3>Step 1: Work one chunk at a time</h3>
            <ul className="help-list">
              <li>Don’t paste a whole report. You’ll get weak output.</li>
              <li>Copy 1–2 paragraphs at a time. It takes longer, but the results are better.</li>
            </ul>
          </div>

          <div className="help-section">
            <h3>Step 2: Use the cheat code, AI</h3>
            <p>Paste your content into your AI tool with this prompt, then edit the result:</p>
            <pre className="help-pre">
Rewrite the following content in plain language that can be understood the first time it’s read. Use everyday words. Keep sentences under 20 words where possible. Use "you" and "we." Break things into bullet points if it helps. Define legal or technical terms in plain English. Make it sound like something you'd say at a help desk. Aim for a 6th grade reading level. Keep the meaning accurate and respectful.

Here’s the text:
[INSERT YOUR TEXT HERE]
            </pre>
            <div className="help-note">Tip: For non-member content, aim for 8th–10th grade instead.</div>
          </div>

          <div className="help-section">
            <h3>Step 3: Use Sentence Scratcher</h3>
            <ul className="help-list">
              <li>Target Grade 6 when you can</li>
              <li>Fix yellow and red sentences</li>
              <li>Split long sentences, or use bullet lists</li>
            </ul>
            <h3>Final checks</h3>
            <ul className="help-list">
              <li>The meaning did not change</li>
              <li>Important details are still there</li>
              <li>The tone feels like how you’d talk to a real person</li>
            </ul>
          </div>
        </div>
      </details>
    </div>
  );
}
