// App.jsx

import React, { useState } from "react";
import TiptapEditor from "./TiptapEditor";

export default function App() {
  const [targetGrade, setTargetGrade] = useState(8);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Sentence Scratcher üêæ</h1>

      <label className="block font-semibold mb-2">Target Grade Level:</label>
      <select
        value={targetGrade}
        onChange={(e) => setTargetGrade(parseInt(e.target.value))}
        className="mb-4 border rounded px-2 py-1"
      >
        {Array.from({ length: 13 }, (_, i) => (
          <option key={i} value={i + 1}>
            Grade {i + 1}
          </option>
        ))}
      </select>

      <TiptapEditor targetGrade={targetGrade} />
    </div>
  );
}
