// src/App.jsx

import React, { useState } from "react";
import TiptapEditor from "./TiptapEditor";

// Grab the version injected at build time
const VERSION = process.env.REACT_APP_VERSION || "dev";

export default function App() {
  const [targetGrade, setTargetGrade] = useState(8);

  return (
    <div className="max-w-4xl mx-auto p-4">
      {/* Title */}
      <h1 className="text-2xl font-bold mb-1">Sentence Scratcher üêæ</h1>

      {/* Version */}
      <div className="text-xs text-gray-500 mb-4">Version {VERSION}</div>

      {/* Grade selector */}
      <label className="block font-semibold mb-2">Target Grade Level:</label>
      <select
        value={targetGrade}
        onChange={(e) => setTargetGrade(Number(e.target.value))}
        className="mb-4 border rounded px-2 py-1"
      >
        {Array.from({ length: 13 }, (_, i) => (
          <option key={i} value={i + 1}>
            Grade {i + 1}
          </option>
        ))}
      </select>

      {/* Editor */}
      <TiptapEditor targetGrade={targetGrade} />
    </div>
  );
}
