import React, { useEffect, useState } from "react";

const THEMES = ["classic", "arcade", "cat", "dog"];

export default function ThemeToggle() {
  const [theme, setTheme] = useState(() => {
    try {
      return localStorage.getItem("ssTheme") || "classic";
    } catch {
      return "classic";
    }
  });

  useEffect(() => {
    // remove any previous theme class, then apply the new one
    document.body.classList.remove(...THEMES.map(t => `theme-${t}`));
    document.body.classList.add(`theme-${theme}`);
    try {
      localStorage.setItem("ssTheme", theme);
    } catch {}
  }, [theme]);

  return (
    <div className="flex gap-2">
      {THEMES.map(t => (
        <button
          key={t}
          className={`btn ${theme === t ? "ring-2 ring-blue-400" : ""}`}
          onClick={() => setTheme(t)}
          type="button"
        >
          {t[0].toUpperCase() + t.slice(1)}
        </button>
      ))}
    </div>
  );
}
