// src/App.jsx
import React, { useEffect, useState } from "react";
import TiptapEditor from "./TiptapEditor";
import ThemeToggle from "./ThemeToggle";

const VERSION = process.env.REACT_APP_VERSION || "";
const GRADE_OPTIONS = [6, 8, 10];

export default function App() {
  const [target, setTarget] = useState(() => {
    try {
      const saved = parseInt(localStorage.getItem("ssTargetGrade") || "6", 10);
      return GRADE_OPTIONS.includes(saved) ? saved : 6;
    } catch {
      return 6;
    }
  });

  const [showHard, setShowHard] = useState(() => {
    try {
      return localStorage.getItem("ssShowHard") !== "0";
    } catch {
      return true;
    }
  });

  const [stats, setStats] = useState({
    words: 0,
    sentences: 0,
    grade: null,
    ease: null,
  });

  // optional toolbar API from editor, buttons hide if not provided
  const [editorAPI, setEditorAPI] = useState(null);

  useEffect(() => {
    try {
      localStorage.setItem("ssTargetGrade", String(target));
    } catch {}
  }, [target]);

  useEffect(() => {
    try {
      localStorage.setItem("ssShowHard", showHard ? "1" : "0");
    } catch {}
  }, [showHard]);

  return (
    <div className="container mx-auto px-4 py-4">
      {/* Title and version */}
      <h1 className="app-title">Sentence Scratcher</h1>
      <div className="version-badge">Version {VERSION}</div>

      {/* Theme buttons row */}
      <div className="mt-2">
        <ThemeToggle />
      </div>

      {/* Optional formatting buttons, only visible if editor exposes API */}
      {editorAPI && (
        <div className="flex flex-wrap items-center gap-2 mt-3">
          <button className="btn" onClick={() => editorAPI.toggleBold()}>Bold</button>
          <button className="btn" onClick={() => editorAPI.toggleItalic()}>Italic</button>
          <button className="btn" onClick={() => editorAPI.toggleH1()}>H1</button>
          <button className="btn" onClick={() => editorAPI.bulletList()}>• List</button>
          <button className="btn" onClick={() => editorAPI.orderedList()}>1. List</button>
        </div>
      )}

      {/* Target grade select */}
      <div className="flex flex-wrap items-center gap-2 mt-3">
        <label htmlFor="target-grade">Target Grade Level:</label>
        <select
          id="target-grade"
          value={target}
          onChange={(e) => setTarget(parseInt(e.target.value, 10))}
        >
          {GRADE_OPTIONS.map((g) => (
            <option key={g} value={g}>Grade {g}</option>
          ))}
        </select>
      </div>

      {/* Legend and likely hard word toggle */}
      <div className="legend-row flex flex-wrap items-center gap-4 mt-3">
        <div className="flex items-center gap-2">
          <span className="legend-red w-3 h-3 border inline-block" />
          <span>Very hard, 25 plus words, or grade plus 3 with 18 plus words</span>
        </div>
        <div className="flex items-center gap-2">
          <span className="legend-yellow w-3 h-3 border inline-block" />
          <span>Slightly over, 20 plus words, or grade plus 1 with 14 plus words</span>
        </div>

        <label className="ml-auto inline-flex items-center gap-2">
          <input
            type="checkbox"
            checked={showHard}
            onChange={(e) => setShowHard(e.target.checked)}
          />
          <span className="legend-underline-purple">Aa</span>
          <span>Likely hard word</span>
        </label>
      </div>

      {/* Stats row */}
      <div className="flex flex-wrap gap-3 text-sm mt-3">
        <div className="stat-chip">Words, {stats.words}</div>
        <div className="stat-chip">Sentences, {stats.sentences}</div>
        <div className="stat-chip">Grade, {stats.grade == null ? "—" : stats.grade.toFixed(1)}</div>
        <div className="stat-chip">Ease, {stats.ease == null ? "—" : stats.ease.toFixed(1)}</div>
      </div>

      {/* Editor lives outside any header wrapper to keep highlights solid */}
      <TiptapEditor
        targetGrade={target}
        showHardWords={showHard}
        onStats={(s) =>
          setStats({
            words: s.wordCount ?? 0,
            sentences: s.sentenceCount ?? 0,
            grade: typeof s.grade === "number" ? s.grade : null,
            ease: typeof s.ease === "number" ? s.ease : null,
          })
        }
        onProvideAPI={(api) => setEditorAPI(api)}
      />
    </div>
  );
}
